import React from "react";
import {
    View,
    Text,
    Image,
    TouchableOpacity, TouchableOpacityComponent
} from "react-native";
import {
    createDrawerNavigator,
    DrawerContentScrollView
} from "@react-navigation/drawer";
import { MainLayout } from '../screens'
import {
    COLORS,
    SIZES,
    FONTS,
    constants,
    icons,
    dummyData
} from "../constants";

const Drawer = createDrawerNavigator()

const CustomDrawerItem = ({label, icon}) => {
    return (
        <TouchableOpacity
            style={{
                flexDirection: 'row',
                height: 40,
                marginBottom: SIZES.base,
                alignItems: 'center',
                paddingLeft: SIZES.radius,
                borderRadius: SIZES.base
            }}
        >
            <Image>
                source={icon}
                style={{
                    width: 20
            }}
            </Image>
        </TouchableOpacity>
    )
}

const CustomDrawerContent = ({navigation}) => {
    return (
        <DrawerContentScrollView
            scrollEnabled={true}
            contentContainerStyle={{ flex: 1 }}
        >
            <View
                style={{
                    flex: 1,
                    paddingHorizontal: SIZES.radius
                }}
            >
                {/* Close Button */}
                <View
                    style={{
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                >
                    <TouchableOpacity
                        style={{
                            alignItems: 'center',
                            justifyContent: 'center'
                        }}
                        onPress={() => navigation.closeDrawer()}
                    >
                        <Image
                            source={icons.cross}

                        />
                    </TouchableOpacity>
                </View>


                {/* Profile */}
                <TouchableOpacity
                    style={{
                        flexDirection: 'row',
                        marginTop: SIZES.radius,
                        alignItems: 'center'
                    }}
                    onPress={() => console.log("Profile")}
                >
                    <Image
                        source={dummyData.myProfile?.profile_image}
                        style={{
                            width: 50,
                            height: 50,
                            borderRadius: SIZES.radius
                        }}
                    />

                    <View
                        style={{
                            marginLeft:SIZES.radius
                        }}
                    >
                        <Text
                            style={{
                                color: COLORS.white,
                                ...FONTS.h3
                            }}
                        >{dummyData.myProfile?.name}</Text>
                        <Text
                            style={{
                                color: COLORS.white,
                                ...FONTS.body4
                            }}>View your profile</Text>
                    </View>
                </TouchableOpacity>


                {/* Drawer Items */}
                <View
                    style={{
                        flex: 1,
                        marginTop: SIZES.padding
                    }}
                >
                    <CustomDrawerItem
                        label={costants.screens.home}
                        icon={icons.home}
                    />
                </View>

            </View>
        </DrawerContentScrollView>
    )
}

const CustomDrawer = () => {
    return (
        <View
            style={{
                flex: 1,
                backgroundColor: COLORS.primary
            }}
        >
                <Drawer.Navigator
                    drawerType={"slide"}
                    overlayColor={"transparent"}
                    drawerStyle={{
                        flex: 1,
                        width: '65%',
                        paddingRight: 20,
                        backgroundColor: 'transparent'
                    }}
                    sceneContainerStyle={{
                        backgroundColor: 'transparent'
                    }}
                    initialRouteName="MainLayout"
                    drawerContent={props => {
                        return (
                            <CustomDrawerContent
                                navigation={props.navigation}
                            />
                        )
                    }}
                >

                    <Drawer.Screen name={"MainLayout"}>
                        {props => <MainLayout {...props}/>}
                    </Drawer.Screen>

                </Drawer.Navigator>
        </View>
    )
}

export default CustomDrawer;
